			event.preventDefault();
			var row = $(this).closest('tr');

			if ($(this).is('.move-up') && $(row).prevAll().length) {
				$(row).insertBefore($(row).prev());
			} else if ($(this).is('.move-down') && $(row).nextAll().length) {
				$(row).insertAfter($(row).next());
			}
		});

